response.action:
items = {{6500,500}} conf = { typeFemale = 269, typeMale = 268, addon = 1, storage = sid.FIRST_NIGHTMARE }
itemsN = 0
for i = 1, table.maxn(items) do item = items[i] if(getPlayerItemCount(cid,item[1])) >= item[2] then itemsN = itemsN + 1 end end if(getPlayerStorageValue(cid, conf.storage)) ~= 1 then if(itemsN == table.maxn(items)) then for i = 1, table.maxn(items) do local item = items[i] doPlayerRemoveItem(cid,item[1],item[2]) end doPlayerAddOutfit(cid, conf.typeFemale,conf.addon) doPlayerAddOutfit(cid, conf.typeMale, conf.addon) setPlayerStorageValue(cid, storage,1) selfSay("Enjoy your newest addon!",cid) else selfSay("You no have the nescessary itens to this addon.",cid) end else selfSay("You already have this addon.",cid) end

